const config: Phaser.Types.Core.GameConfig = {
    // Create a new instance of the Phaser.Game class

        type: Phaser.AUTO,
        width: 480,
        height: 640,
        scene:[Game, GameOver],
        // preload: (Game) =>{
        //   // Preload the assets using the load.image() method
        //   Game.load.image('background', 'bg_layer1')
        //   Game.load.image('platform', 'ground_grass')
        //   Game.load.image('bunny-stand', 'bunny1_stand')
        //   Game.load.image('bunny-jump', 'bunny1_jump')
        //   Game.load.image('carrot', 'carrot')
        //   Game.load.audio('jump', 'phaseJump1')
        // },

        physics: {
            default: 'arcade',
            arcade: {
                gravity: {
                    y: 200
                },
                debug: true
            }
        }

  }


  // render() {
  //   return <div id="game" />;
  // }
// }

export default new Phaser.Game(config);
